
public class OperatorBasic2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		연산자(operator)
//		
//		연산자: 연산을 수행하는 기호(+, - 등)
//		피연산자: 연산자의 작업 대상(변수, 상수, 리터럴 등)
		
		int firstNum = 0;
		double secondNum = 0;
		
		firstNum = 10;
		secondNum = 4;
		
		System.out.println(firstNum + secondNum);
		System.out.println(firstNum * secondNum);
		System.out.println(firstNum / secondNum);	//int끼리는 정수만 표현, 소수점 버림. double로 바꿔주면 제대로 계산됨
		System.out.println(firstNum / 4.0);			//실수(여기선 더블)를 넣어줘야 자동 형변환이 일어나면서 제대로 계산됨
		
				
	}
}
