
public class TernaryOperatorBasic1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		삼항연산자
		
//		조건 연산자 ?, :
//			
//		표현식
//		조건식 ? 식1 : 식2
//		조건식이 참이면 식1을 반환한다
//		조건식이 거짓이면 식2를 반환한다
		
		int x = 4;
		int y = 2;
		int resultNum = 0;
		
		resultNum = (x > y) ? x : y;
		
		System.out.println("두 숫자 중 큰 값은?");
		System.out.println(resultNum);
		
		
		
		
		
	}

}
