
public class FloatBasic1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		실수형
//		float	4byte	7자리
//		double	8byte	15자리
		
		float f = -1.12345f;	// 군사작전이나 시뮬레이터 같은 곳에서 씀. 수포자는 못 가는 곳에서 씀
		double d = 12.098765;	// 이거도 절삭할 때 쓰이곤 하는데 너가 쓸 일은 거의 없을 것임.
								// 더블도 뒤에 d가 생략되어 있는 것임.
		System.out.println(f);
		System.out.println(d);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
