
public class VarMateInfoTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "안병규";
		int age = 30;
		System.out.println(name);
		System.out.println(age);		
			
		name = "오건황";
		System.out.println(name);
		System.out.println(age);	
		
		name = "이동현";
		age = age - 3;
		System.out.println(name);
		System.out.println(age);	
				
		
	}

}
